        static WaterData()
        {
            //LOD post processing
            for (int i = 0; i < VolumetricLOD.Length; i++)
            {
                if (VolumetricLOD[i].LodLevel <= WaterTreeNode.MIN_DEPTH)
                    VolumetricLOD[i].Distance = double.MaxValue;
                /*else
                    VolumetricLOD[i].Distance = Math.Pow(VolumetricLOD[i].Distance, 2);*/
            }

            MaxLODDepth = VolumetricLOD[VolumetricLOD.Length - 1].LodLevel;
        }

